mapper.select:
select * from course


        course_name like concat('%', #{courseName}, '%')


        and end_date >= #{begin}


        and end_date <= #{end}


order by create_time desc

mapper.update:
update course set
      course_name=#{courseName}, room=#{room}, begin_date=#{beginDate}, end_date=#{endDate},
      head_teacher_id=#{headTeacherId}, create_time=#{createTime}, update_time=#{updateTime},
      head_teacher_name=#{headTeacherName}, course_status=#{courseStatus}

        , subject=#{subject}

where id=#{id}
